/* please refer to search.js for instructions */
var searchForm=document.getElementById("search"),searchInput=searchForm.querySelector("input");if(!searchForm||!searchInput)throw new Error("The search form isnt properly configured!");
searchForm.addEventListener("submit",function(event){var tag="";if(searchInput.value[0]==="!"){var temp=searchInput.value.indexOf(" ");tag=searchInput.value.substr(1,temp-1);searchInput.value=searchInput.value.substr(temp+1,searchInput.value.length)}
switch(tag){
case 'd':
case 'ddg':
case 'duckduckgo':
	searchForm.setAttribute('action', 'https://duckduckgo.com/');
	searchInput.setAttribute('name', 'q');
	break;

case 'b':
case 'bing':
	searchForm.setAttribute('action', 'https://www.bing.com/search')
	searchInput.setAttribute('name', 'q');
	break;

case 'g':
case 'google':
default:
	searchForm.setAttribute('action', 'https://www.google.com/search')
	searchInput.setAttribute('name', 'q');
	break;
}});